package org.tnsis.operator;

public class BitwiseOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=13, y=9;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(x<<y);
		System.out.println(x>>y);
		System.out.println(x^y);

	}

}
